<h2>Products</h2>
<div *ngIf="getCategory() == 'all'">
  <div *ngFor="let product of products" style="margin-bottom: 20px;" id="products">
  <img src={{product.image}}>
  <h3>
    <a
      [title]="product.name + ' details'"
      [routerLink]="['/products', product.id]"
    >
      {{ product.name }}
    </a>
  </h3>

  <p *ngIf="product.description">Description: {{ product.description }}</p>
  <p *ngIf="product.rating">Rating: {{product.rating}}</p>
  <a href="https://wa.me/?text={{product.link}}" target="_blank"><button type="button">Share</button></a>
  <app-product-alerts [product]="product" (notify)="onNotify()">
  </app-product-alerts>
  </div>
</div>
<div *ngFor="let product of getFiltered()" style="margin-bottom: 20px;" id="products">
  <img src={{product.image}}>
  <h3>
    <a
      [title]="product.name + ' details'"
      [routerLink]="['/products', product.id]"
    >
      {{ product.name }}
    </a>
  </h3>

  <p *ngIf="product.description">Description: {{ product.description }}</p>
  <p *ngIf="product.rating">Rating: {{product.rating}}</p>
  <a href="https://wa.me/?text={{product.link}}" target="_blank"><button type="button">Share</button></a>
  <app-product-alerts [product]="product" (notify)="onNotify()">
  </app-product-alerts>
  </div>
