import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

import { Product, products } from '../products';
import { CartService } from '../cart.service';
@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css'],
})
export class ProductDetailsComponent implements OnInit {
  product: Product | undefined;
  like = 0;
  constructor(
    private route: ActivatedRoute,
    private cartService: CartService
  ) {}
  addToCart(product: Product) {
    this.cartService.addToCart(product);
    window.alert('Your product has been added to the cart!');
  }
  clickLike(product: Product){
    if(this.product != undefined){
      this.product.like++;
    }
  }
  ngOnInit() {
    // First get the product id from the current route.
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));

    // Find the product that correspond with the id provided in route.
    this.product = products.find(
      (product) => product.id === productIdFromRoute
    );
  }
}
<h2>Product Details</h2>

<div *ngIf="product">
  <h3>{{ product.name }}</h3>
  <h4>{{ product.price | currency }}</h4>
  <p>{{ product.description }}</p>
  <div>
  <img src={{product.img1}} style="width:  150px;height: 300px;float: left;">
  <img src={{product.img2}} style="width:  150px;height: 300px;float: left;margin-left:1cm">
  <img src={{product.img3}} style="width:  150px;height: 300px;float: left;margin-left:1cm;">
  </div>
  <button type="button" (click)="addToCart(product)" style="margin-top:1cm">Buy</button>
  <a href={{product.link}} target="_blank" style="margin-left:1cm"><button>kaspi</button></a>
  <button style="margin-left:1cm;background-color: rgb(253, 113, 113);" (click)="clickLike(product)">like {{product.like}}</button>
  <button>remove</button>
  <!-- <button (click)="window.location.href=product.link">kaspi</button> -->
</div>
